styles
// Simple rules
.container {
        padding: 24px;
        margin: 0 auto;
        max-width: 1200px;
    }
// Multiple selectors
.card, .panel, .box {
        background: var(--card-bg);
        border-radius: var(--radius);
        box-shadow: var(--shadow-md);
    }
// Nested-like selectors (using &)
.button {
        padding: 8px 16px;

        &:before {
            content: "";
            width: 10px;
            height: 10px
        }

        & .nested-1 {
            & .nested-2.optional {
                background: blue
            }
        }
    }
// State interpolation in CSS
.themed {
        color: {state.theme.primaryColor};
        background: {state.theme.bgColor};
        font-size: {state.settings.fontSize + 1};
    }
// Pseudo-classes and pseudo-elements
.link:hover {
        text-decoration: underline;
    }
.input:focus {
        outline: 2px solid var(--primary);
    }
.item:first-child {
        margin-top: 0;
    }
// Attribute selectors
[data-theme="dark"] {
        background: #1a1a1a;
    }
// ID selectors
#main-content {
        flex: 1;
    }
.card-header {
        border-bottom: 1px solid var(--border);
    }
.item.active {
        margin-top: 8px;
    }
// @media queries
@media (min-width: 768px) {
        .container {
            padding: 32px;
        }

        .grid {
            grid-template-columns: repeat(2, 1fr);
        }
    }
@media (min-width: 1024px) {
        .grid {
            grid-template-columns: repeat(3, 1fr);
        }
    }
// @keyframes animations
@keyframes fadeIn {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }
@keyframes slideUp {
        0% {
            transform: translateY(20px);
            opacity: 0;
        }
        100% {
            transform: translateY(0);
            opacity: 1;
        }
    }
// @layer for CSS Cascade Layers
@layer base, components, utilities {
        .reset {
            margin: 0;
            padding: 0;
        }
    }
// @supports for feature detection
@supports (display: grid) {
        .layout {
            display: grid;
        }
    }
// @container queries
@container (min-width: 400px) {
        .card-content {
            padding: 24px;
        }
    }
// CSS variables in values
.themed {
        color: var(--text-primary);
        background: var(--bg-secondary);
        border-color: var(--border-color);
    }
// CSS functions
.computed {
        width: calc(100% - 32px);
        height: min(100vh, 800px);
        font-size: clamp(14px, 2vw, 18px);
    }

styles
* {
        box-sizing: border-box;
    }
html {
        font-size: 16px;
    }
body {
        margin: 0;
        font-family: system-ui, sans-serif;
    }
