elements.raw_css:
styles
.catalog-page {
        min-height: 100vh;
    }
.page-header {
        padding: 16px 24px;
        border-bottom: 1px solid var(--border);
    }
.product-search {
        width: 300px;
    }
.catalog-container {
        padding: 24px;
    }
.filter-sidebar {
        width: 280px;
        background: var(--card-bg);
        border-radius: var(--radius);
        padding: 24px;
    }
.filter-section {
        margin-bottom: 16px;
    }
.catalog-main {
        flex: 1;
    }
.catalog-toolbar {
        margin-bottom: 24px;
    }
.product-grid {
        gap: 24px;
    }
.product-card {
        transition: transform 0.2s;
    }
.product-image-container {
        position: relative;
        overflow: hidden;
    }
.discount-badge {
        position: absolute;
        top: 8px;
        left: 8px;
    }
.product-category {
        color: var(--text-muted);
    }
.current-price {
        font-weight: 600;
        color: var(--primary);
    }
