styles
.registration-page {
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 32px;
        background: var(--bg-subtle);
    }
.registration-card {
        width: 100%;
        max-width: 500px;
        background: var(--card-bg);
        border-radius: var(--radius-lg);
        box-shadow: var(--shadow-lg);
    }
.registration-header {
        text-align: center;
        padding: 32px 32px 16px;
    }
.registration-content {
        padding: 24px 32px;
    }
.step-content {
        display: flex;
        flex-direction: column;
        gap: 20px;
    }
.form-field {
        display: flex;
        flex-direction: column;
        gap: 6px;
    }
.form-actions {
        margin-top: 24px;
        padding-top: 24px;
        border-top: 1px solid var(--border);
    }
.registration-footer {
        text-align: center;
        padding: 16px 32px 32px;
    }
